mapper.update:
update booklend


        book_name = #{bookName},


        lend_time = #{lendTime},


        return_time = #{returnTime},


        borrower = #{borrower}


where company_id = #{companyId} and book_record = #{bookRecord};

mapper.select:
select count(*)
from booklend where 1=1

    and company_id = #{companyId}


    and book_record = #{bookRecord}


    and book_name = #{bookName}


    and lend_time = #{lendTime}


    and return_time = #{returnTime}


    and borrower = #{borrower}
