mapper.update:
update member
name = #{name}, phone_number = #{phoneNumber}, email = #{email}, grade = #{grade}, sex = #{sex}, profession = #{profession}, department = #{department},
where company_id = #{companyId} and num = #{num};

mapper.select:
select company_id, num, name, phone_number, email, grade, sex, profession, department
from member
where company_id = #{companyId} and num = #{num};

select company_id, num, name, phone_number, email, grade, sex, profession, department
from member where company_id = #{companyId}

    limit #{offset},#{limit}
