mapper.select:
select company_id, project_id, project_name, project_url, online_time
  from project where 1=1

    and company_id = #{companyId}


    and project_id = #{projectId}


    and project_name = #{projectName}


    and project_url = #{projectUrl}


    and online_time = #{onlineTime}

mapper.update:
update project


        project_name = #{projectName},


        project_url = #{projectUrl},


        online_time = #{onlineTime}


where company_id = #{companyId} and project_id = #{projectId}

mapper.select:
select company_id, project_id, project_name, project_url, online_time
from project where 1=1

    and company_id = #{project.companyId}


    and project_id = #{project.projectId}


    and project_name = #{project.projectName}


    and project_url = #{project.projectUrl}


    and online_time = #{project.onlineTime}

limit #{offset},#{size}
