mapper.update:
update whereabout
company_id = #{companyId}, num = #{num}, name = #{name}, phone_number = #{phoneNumber}, email = #{email}, grade = #{grade}, sex = #{sex}, profession = #{profession}, department = #{department}, work_place = #{workPlace},
where company_id = #{companyId} and num = #{num};

mapper.select:
select company_id, num, name, phone_number, email, grade, sex, profession, department, work_place
from whereabout
where company_id = #{companyId} and num = #{num};

select company_id, num, name, phone_number, email, grade, sex, profession, department, work_place
from whereabout
where grade = #{grade}

    limit #{pager.offset},#{pager.limit}

select company_id, num, name, phone_number, email, grade, sex, profession, department, work_place from whereabout
where name like '%${name}%'
limit #{pager.offset},#{pager.limit}

select company_id, num, name, phone_number, email, grade, sex, profession, department, work_place
from whereabout

    limit #{offset},#{limit}
